CREATE  PROCEDURE `sp_CopyOrder`(
	i_Id BIGINT,
	i_NewId BIGINT
)
BEGIN	
	-- copy order info
	INSERT INTO `Order`( Id
						,EmployeeId
						,EmployeeName
						,Step
						,Taxes
						,Surcharge
						,Discount
						,OrderDate
						,Notes
						,SupplierId
						,SupplierName
						,SupplierAddress
						,SupplierEmail
						,SupplierPhone
						,OrderRefNo
						,OrderType
						,CreateDTS
						,ActiveStatus)

	SELECT i_NewId
		,EmployeeId
		,EmployeeName
		,2 -- Submitted
		,Taxes
		,Surcharge
		,Discount
		,NOW()
		,Notes
		,SupplierId
		,SupplierName
		,SupplierAddress
		,SupplierEmail
		,SupplierPhone
		,OrderRefNo
		,OrderType
		,NOW()
		,ActiveStatus
	FROM `Order`
	WHERE Id=i_Id;

	-- Copy components that step is new(id=1)
	INSERT INTO OrderComponent(	OrderId
								,OrderType
								,ComponentId
								,ColorId
								,Quantity
								,Price
								,ExtCharge
								,UnitId
								,Step
								,AMTExcGST
								,GST
								,AMTIncGST
								,CreateDTS
								)
	SELECT i_NewId
			,OrderType
			,ComponentId
			,ColorId
			,IFNULL(Quantity,0) - IFNULL(Received,0)
			,Price
			,ExtCharge
			,UnitId
			,Step
			,AMTExcGST
			,GST
			,AMTIncGST
			,NOW()
	FROM OrderComponent
	WHERE OrderId = i_Id AND IFNULL(Quantity,0) > IFNULL(Received,0);

	-- Copy product that step is new(id=1)
	INSERT INTO OrderProduct (	OrderId
								,OrderType
								,ProductId
								,MaterialId
								,LocationId
								,ColorId
								,ControlSideId
								,UnitId
								,`Drop`
								,Width
								,Quantity
								,Discount
								,ExtendPrice
								,UnitPrice
								,TotalAmount
								,Received
								,BackOrder
								,Step
								,AMTExcGST
								,GST
								,AMTIncGST
								,CreateDTS
								,ActiveStatus
								)
	SELECT i_NewId
			,OrderType
			,ProductId
			,MaterialId
			,LocationId
			,ColorId
			,ControlSideId
			,UnitId
			,`Drop`
			,Width
			,IFNULL(Quantity,0) - IFNULL(Received,0)
			,Discount
			,ExtendPrice
			,UnitPrice
			,TotalAmount
			,Received
			,BackOrder
			,Step
			,AMTExcGST
			,GST
			,AMTIncGST
			,NOW()
			,ActiveStatus
	FROM OrderProduct
	WHERE OrderId = i_Id AND IFNULL(Quantity,0) > IFNULL(Received,0);

	-- Step 2: update amount
	CALL sp_CalculateOrderAmount (i_NewId);
END ;;
