		,oc.UnitId
		,oc.UnitName
		,oc.TotalAmount
		,oc.Discount
		,oc.OrderId
		,oc.Size
		,NOW()
	FROM XMLColumns t
	JOIN OrderComponent oc ON t.Id = oc.Id;
    
