    i_PaymentType NVARCHAR(250),
	i_DatePaid DATETIME,
	i_ActiveStatus INT
)
BEGIN	

	DECLARE i_TotalPaid DECIMAL(18,2);
	DECLARE	i_TotalReceived DECIMAL(18,2);
    
