	DELETE SP 
    FROM SalaryPayment sp
    JOIN XMLColumns x ON x.EmployeeId = sp.EmployeeId
	WHERE sp.PaidFromDate BETWEEN i_FromDate AND i_ToDate;

	-- step 2: insert new data
