	DECLARE i_OdId BIGINT;

	UPDATE OrderComponent
	SET
        OrderId=IFNULL(i_OrderId,OrderId),
		ComponentId=IFNULL(i_ComponentId,ComponentId),
		ColorId=IFNULL(i_ColorId,ColorId),
		Quantity=IFNULL(i_Quantity,Quantity),
